import React, { Component } from 'react'
import mapboxgl from 'mapbox-gl'
import '../map.css'

mapboxgl.accessToken =
  'pk.eyJ1Ijoid2lsbGQxMCIsImEiOiJjazVrZjgweGUwZGpiM2RucnB6ZW83cnF1In0.QuvhJzfhJrxg-dNfhVrJ7A'

class Map extends Component {
  constructor(props) {
    super(props)
    this.state = {
      lng: props.lon,
      lat: props.lat,
      zoom: 9
    }
  }

  makeGeoJSON = (trail) => {

    return (
      {
        type: 'Feature',
        geometry: {
          type: 'Point',
          coordinates: [trail.longitude, trail.latitude]
        },
        properties: {
          id: trail.id,
          name: trail.name,
          city: trail.location,
          summary: trail.summary,
          difficulty: trail.difficulty,
          stars: trail.stars,
          img_url: trail.imgSmall,
          length: trail.length,
          condition: trail.conditionStatus,
          conditionDate: trail.conditionDate	
        }
      }
    )
  }

  componentDidMount() {
    let trailsUrl = 'https://www.hikingproject.com/data/get-trails?lat=' + `${this.state.lat}` + '&lon=' + `${this.state.lng}` + '&maxDistance=10&key=200670860-5b467b02cad74880f091a3ea5d36d611'
    fetch(trailsUrl)
    .then(res => res.json())
    .then(
      (result) => {
        result.trails.forEach(trail => stores.features.push(this.makeGeoJSON(trail)))

      } 
    )

    let stores = {
      type: 'FeatureCollection',
      features: []
    }

    /**
     * Assign a unique id to each store. You'll use this `id`
     * later to associate each point on the map with a listing
     * in the sidebar.
     */
    stores.features.forEach(function(store, i) {
      store.properties.id = i
    })

    console.log(stores)
    const map = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom
      // scrollZoom: true
    })

    map.on('move', () => {
      this.setState({
        lng: map.getCenter().lng.toFixed(4),
        lat: map.getCenter().lat.toFixed(4),
        zoom: map.getZoom().toFixed(2)
      })
    })

    /**
     * Add geolocate control to the map.
     **/
    map.addControl(
      new mapboxgl.GeolocateControl({
        positionOptions: {
          enableHighAccuracy: true
        },
        trackUserLocation: true
      })
    )

    /**
     * Add zoom and rotation controls to the map.
     **/
    map.addControl(new mapboxgl.NavigationControl())

    /**
     * Wait until the map loads to make changes to the map.
     */
    map.on('load', function(e) {
      /**
       * This is where your '.addLayer()' used to be, instead
       * add only the source without styling a layer
       */
      map.addSource('places', {
        type: 'geojson',
        data: stores
      })

      /**
       * Add all the things to the page:
       * - The location listings on the side of the page
       * - The markers onto the map
       */
      buildLocationList(stores)
      addMarkers()
    })

    /**
     * Add a listing for each store to the sidebar.
     **/
    function buildLocationList(data) {
      const listings = document.getElementById('listings')
        listings.innerHTML = ""
      data.features.forEach(function(store, i) {
        /**
         * Create a shortcut for `store.properties`,
         * which will be used several times below.
         **/
        const prop = store.properties

        /* Add a new listing section to the sidebar. */
        const listing = listings.appendChild(document.createElement('div'))
        /* Assign a unique `id` to the listing. */
        listing.id = 'listing-' + prop.id
        /* Assign the `item` class to each listing for styling. */
        listing.className = 'item'

        /* Add the link to the individual listing created above. */
        const link = listing.appendChild(document.createElement('a'))
        link.href = '#'
        link.className = 'name'
        link.id = 'link-' + prop.id
        link.innerHTML = prop.name

        /* Add details to the individual listing. */
        const details = listing.appendChild(document.createElement('div'))
        details.innerHTML = prop.stars

        const extraDetails = listing.appendChild(document.createElement('div'))
        extraDetails.innerHTML = prop.city
        extraDetails.innerHTML += ' Â· ' + prop.length + ' mile(s)'

        /**
         * Listen to the element and when it is hovered, do four things:
         * 1. Update the `currentFeature` to the store associated with the clicked link
         * 2. Fly to the point
         * 3. Close all other popups and display popup for clicked store
         * 4. Highlight listing in sidebar (and remove highlight for all other listings)
         **/

        link.addEventListener('mouseover', function(e) {
          for (let i = 0; i < data.features.length; i++) {
            if (this.id === 'link-' + data.features[i].properties.id) {
              var clickedListing = data.features[i]
              flyToStore(clickedListing)
              createPopUp(clickedListing)
            }
          }
          const activeItem = document.getElementsByClassName('active')
          if (activeItem[0]) {
            activeItem[0].classList.remove('active')
          }
          this.parentNode.classList.add('active')
        })
      })
    }

    /**
     * Add a marker to the map for every store listing.
     **/
    function addMarkers() {
      /* For each feature in the GeoJSON object above: */
      stores.features.forEach(function(marker) {
        /* Create a div element for the marker. */
        var el = document.createElement('div')
        /* Assign a unique `id` to the marker. */
        el.id = 'marker-' + marker.properties.id
        /* Assign the `marker` class to each marker for styling. */
        el.className = 'marker'

        /**
         * Create a marker using the div element
         * defined above and add it to the map.
         **/
        new mapboxgl.Marker(el, { offset: [0, -23] })
          .setLngLat(marker.geometry.coordinates)
          .addTo(map)

        /**
         * Listen to the element and when it is clicked, do three things:
         * 1. Fly to the point
         * 2. Close all other popups and display popup for clicked store
         * 3. Highlight listing in sidebar (and remove highlight for all other listings)
         **/
        el.addEventListener('click', function(e) {
          /* Fly to the point */
          flyToStore(marker)
          /* Close all other popups and display popup for clicked store */
          createPopUp(marker)
          /* Highlight listing in sidebar */
          var activeItem = document.getElementsByClassName('active')
          e.stopPropagation()
          if (activeItem[0]) {
            activeItem[0].classList.remove('active')
          }
          var listing = document.getElementById(
            'listing-' + marker.properties.id
          )
          listing.classList.add('active')
        })
      })
    }
    /**
     * Use Mapbox GL JS's `flyTo` to move the camera smoothly
     * a given center point.
     **/
    function flyToStore(currentFeature) {
      map.flyTo({
        center: currentFeature.geometry.coordinates,
        zoom: 15
      })
    }

    /**
     * Create a Mapbox GL JS `Popup`.
     **/
    function createPopUp(currentFeature) {
      var popUps = document.getElementsByClassName('mapboxgl-popup')
      if (popUps[0]) popUps[0].remove()
      var popup = new mapboxgl.Popup({ closeOnClick: false })
        .setLngLat(currentFeature.geometry.coordinates)
        .setHTML(
          '<h3>' + currentFeature.properties.name + '</h3>' +
            '<h4>' + currentFeature.properties.difficulty + '</h4>' +
            '<h4>' + currentFeature.properties.summary + '</h4>' +
            "<img src='${currentFeature.properties.img_url}`' />" +
            '<h4>' + currentFeature.properties.condition + '</h4>'
        )
        .addTo(map)
    }
  }




  render() {
    return (
      <div>
        <h3>FROM MAP.JS</h3>
        {/* <div className="sidebarStyle">
          <div>
            Longitude: {this.state.lng} | Latitude: {this.state.lat} | Zoom:{' '}
            {this.state.zoom}
          </div>
        </div> */}

        <div className="sidebar">
          <div className="heading">
            <h1>Hiking Trails</h1>
          </div>
          <div id="listings" className="listings"></div>
        </div>
        <div ref={el => (this.mapContainer = el)} className="map" />
        {/* <div id='map' class='map'></div> */}
      </div>
    )
  }
}

export default Map
