import React, { Component } from 'react'
import Map from './components/Map'
import './App.css'
import Navigation from './components/Navigation'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
// import Stores from './stores'
import Login from './components/Login'
import Homepage from './components/Homepage'
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      location: "",
      longitude: "",
      latitude: ""
    }
  }

  handleUserLocation = (props) => {
    this.setState({
      location: props
    })
    console.log(this.state.location)
    fetch('https://us1.locationiq.com/v1/search.php?key=6d623611deb8e1&q=san%20francisco&format=json')
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          longitude: result[0].lat,
          latitude: result[0].lon
        })
      }
    )
  }

  componentDidMount() {
    // fetch(Stores)
    // .then(res => res.json())
    // .then(res => console.log(res.features))
    // // .then(store => this.setState({ pokemonCollection: pokemonCollection }))
    // .catch(e => console.error(e))

  }

  render() {
    return (
      <div className="App">
        <Router>
        <Navigation />
        <Switch>
        <Route exact path="/" component={() => <Homepage location={this.handleUserLocation} />} />
        <Route exact path="/hikingtrails" component={() => <Map />} />
        <Route exact path="/login" component={() => <Login />} />
        </Switch>
        </Router>
      </div>

    )
  }
}

export default App
